    (
     #{createTime}, #{updateTime}, #{isDeleted}, #{userId}, #{skuId}
     )

mapper.select:
select user_id, sku_id
from user_collect
where is_deleted = 0 and user_id = #{userId} and sku_id = #{skuId}
